import React from 'react';
import PropTypes from 'prop-types';
import { Image, Platform, StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import { Avatar } from 'react-native-paper';

const UserInsight = props => {
	return (
		<View style={styles.container}>
			{props.userImage !== null || props.userImage !== undefined  ? <Avatar.Image size={24} source={props.userImage} /> : <Avatar.Text size={70} label="XD" /> }
		</View>
	);
};

const styles = StyleSheet.create({
	container: {display: 'flex', justifyContent: 'center', width: '90%', marginLeft: 'auto', marginRight: 'auto'},
})

UserInsight.propTypes = {
	userImage: PropTypes.string
};

export default UserInsight;
